package com.AssginmentOPertor;

public class AssginOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a;
		int b=20;
		a=b;
		System.out.println("a="+a+  "\tb="+b);
		a+=b;
		System.out.println("a="+a+  "\tb="+b);
		a-=b;
		System.out.println("a="+a+  "\tb="+b);
		a*=b;
		System.out.println("a="+a+  "\tb="+b);
		a/=b;
		System.out.println("a="+a+  "\tb="+b);
		a%=b;
		System.out.println("a="+a+  "\tb="+b);
	


	}

}
