package com.Basic;

public class Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=20;
		int b=30;
		System.out.println("After the Adition: "+(a+b));
		System.out.println("After the Subtraction: "+(a-b));
		System.out.println("After the division: "+(a/b));
		System.out.println("After the mod: "+(a%b));
		System.out.println("After the Multipication: "+(a*b));

	}

}
