package com.Basic;

public class Swaping {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 20;
		int c = 0;
		System.out.println("Before Swaping  the  variable: "+"A="+a+"\nB="+b);
		c = a;
	    a = b;
	    b = c;
		
		System.out.println("After Swaping  the  variable: "+"A=\n"+a+"\nB="+b);
		

	}

}
