package com.operator;

public class AssArithmaticOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a  ,b = 2;
		a = b;
		System.out.println("a= "+a+" b= "+b+" Afer a=b ");
		
		a+=b;
		System.out.println("a= "+a+" b= "+b+" Afer a+=b ");
		
		a-=b;
		System.out.println("a= "+a+" b= "+b+" Afer a-=b ");
		
		a*=b;
		System.out.println("a= "+a+" b= "+b+" Afer a*=b ");
		
		a/=b;
		System.out.println("a= "+a+" b= "+b+" Afer a/=b ");
		
		a%=b;
		System.out.println("a= "+a+" b= "+b+" Afer a%=b ");
		

	}

}
