package com.operator;

public class TryOp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 20;
		int num2 = 2;
		System.out.println(num1&num2);
		System.out.println(num1|num2);
		System.out.println(num1^num2);             
		System.out.println(~num2);					
		System.out.println("-"+(++num2));
		System.out.println(num1<<2);
		System.out.println(num1>>2);
		
		
	}

}
