package com.operator;

public class UnaryOp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int c = 20;
		System.out.println("The value of c is: "+c++);
		c++;
		System.out.println("The value of c is: "+c);
		System.out.println("The value of c after in prrfix: "+(++c));
		

	}

}
