package practicejava;

public class Pattren5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		for(int i=a;i>=1;--i) {
			for(int j=1;j<=i;++j) {
				System.out.print(" *");
			}
			System.out.println();
			//for(int k=1;k<a+1;k++) {
				//System.out.println(" ");
			//normal triangle
		}

	}

}
